mapper.select:
select



,




from CUST_AMC_CMPYCONTACTOR cac
inner join CUST_TRD_INFO cti on cti.buyer_id = cac.cmpy_id
and cti.buyer_type = 2




  ${filterByClause}


  where ${whereClause}


  order by ${orderByClause}

select
from CUST_AMC_CMPYCONTACTOR cac where cac.company = #{company} and cac.name = #{name}
and ( 1=2
OR cac.trd_phone LIKE CONCAT('%',#{item},'%') OR cac.mobile_update LIKE CONCAT('%',#{item},'%') OR cac.mobile_prep LIKE CONCAT('%',#{item},'%') OR cac.mobile_history LIKE CONCAT('%',#{item},'%')
)
and ( 1=2
OR cac.trd_phone LIKE CONCAT('%',#{item},'%') OR cac.phone_update LIKE CONCAT('%',#{item},'%') OR cac.phone_prep LIKE CONCAT('%',#{item},'%') OR cac.phone_history LIKE CONCAT('%',#{item},'%')
)
